import { Routes, Route } from 'react-router-dom'
import Dashboard from './pages/Dashboard'
import NavBar from './components/NarBar'
import { useEffect, useState } from 'react'
import ROSLIB from 'roslib'

function Router() {
  const [message, setMessage] = useState(null)

  useEffect(() => {
    const ros = new ROSLIB.Ros({
      url: 'ws://169.254.5.90:9090'
    });

    ros.on('connection', () => console.log("Connected to ROS"));
    ros.on('error', (error) => console.error('ROS Error: ', error))
    ros.on('close', () => console.log('Connection closed'))

    const listener = new ROSLIB.Topic({
      ros,
      name: "/tts/phrase",
      messageType: "std_msgs/String"
    });

    listener.subscribe((msg) => {
      console.log('Received message: ', msg)
      setMessage(msg.data)
    })

    return () => {
      listener.unsubscribe()
      ros.close()
    }
  }, [])
  return (
    <>
      <NavBar></NavBar>
      <Routes>
        <Route path='/' element={<Dashboard message={message}/>}/>
      </Routes>
    </>
  )
}

export default Router
